import { useEffect, useState } from 'react';
import IpAddress from '../../../IpAddress';
import ServerTab from './ServerTab';

import socketIOClient from "socket.io-client";
var ip = new IpAddress();
let socket = socketIOClient(`http://${ip.getIP()}:3001`)

function ServerList(){
    const [servers, setServers] = useState<any>([]);

    useEffect(() => {
        socket.emit("client:get-server");
        socket.on(`server:active-server`, data => {
            data.id = servers.length;
            console.log(servers);
            servers.push(data);
        })
    }, []);
    return (
        servers.forEach(function (server: any) {
            console.log(server);
          })
    );
    /*
    return <>{
        servers.map((server: {Image: string, Name: string, State: boolean, id: number}) => 
        <div key={server.id}>{
            <ServerTab key={server.id} server={server} />
        }</div>)
      }</>*/
}
export default ServerList;
