import { useEffect, useState } from 'react';
import IpAddress from '../../../IpAddress';
import ServerTab from './ServerTab';

import socketIOClient from "socket.io-client";
var ip = new IpAddress();
let socket = socketIOClient(`http://${ip.getIP()}:3001`)

function ServerList(){
    const [servers, setServers] = useState<any>();

    useEffect(() => {
        socket.emit("client:active-servers")
        socket.on(`server:active-server`, server => {
            setServers(servers.list.concat(server));
        })
    }, []);
    return <>{
        servers.map((server: {Image: string, Name: string, State: boolean, id: number}) => <>{
            <ServerTab 
                serverImage={server.Image} 
                serverName={server.Name} 
                serverState={server.State} 
                serverID={server.id} 
                key={server.id}
            />
        }</>)
      }</>
}
export default ServerList;
