import { Key, useEffect, useState } from "react";
import Player from './Player';

function PlayerList(props: {players: any, activePlayer: any, onPlayerClick: any;}){
    const [players, setPlayers] = useState<any>(props.players);
    console.log(players)

    useEffect(function updatePlayers() {
        setPlayers(JSON.parse(props.players));
    }, [props.players]);

    return (
        players.map((player: { UUID: Key | null | undefined; playerName: Key | null | undefined; }) => 
        <div key={player.UUID}>{
            <Player 
                player={player} 
                key={player.playerName}
                onPlayerClick={props.onPlayerClick(player)} 
                activePlayer={props.activePlayer}
            />
        }</div>)
    );
}
export default PlayerList;
