import { FormControlLabel, Radio, RadioGroup, TextField, Tooltip } from '@mui/material';
import { DateTimePicker, LocalizationProvider } from '@mui/x-date-pickers';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';

import './BanPanelStyle.scss';

import { useEffect, useState } from 'react';

import {socket} from "service/socket";

function BanPanel(props: {socket: any, player: any;}){
    const [error, setError] = useState<boolean>(false);
    const [message, setMessage] = useState<string>("");
    const [banMessage, setBanMessage] = useState<string>("");
    const [value, setValue] = useState<any>("permanent");
    const [date, setDate] = useState<Date | null>(null);

    function banPlayer(){
        if(value === "duration" && date == null){
            setError(true);
            setInfoMessage("Geef een datum in indien je kiest voor een bepaalde tijd!");
            return;
        }
        var data = {
            Player: props.player,
            Feature: "ban",
            Message: banMessage,
            To: date?.toISOString()
        }
        socket.emit("client:features-change", data);
        setBanMessage("");
    }
    useEffect(function listenMessages(){
        socket.on(`server:features-change-message`, data => {
            if (data.includes("fout"))setError(true);
            else setError(false);
            setInfoMessage(data);
        })
    }, []);
    function setInfoMessage(data: string){
        setMessage(data);
        setTimeout(function(){
            if (message !== data)
                setMessage("");
        }, 5000)
    }
    function handleMessageChange (e: any) {
        setBanMessage(e.target.value)
    }
    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setValue((event.target as HTMLInputElement).value);
        if ((event.target as HTMLInputElement).value === "permanent")setDate(null);
    };
    return (
        <>
            <div className='panel-header'>
                Ban Panel - {props.player.Displayname}
            </div>
            <div className='panel-line'></div>
            <div className='banpanel-container'>
                <form className='banpanel-form'>
                    <input type="text" onChange={handleMessageChange} placeholder="Geef ban message..." value={banMessage} />
                    <div className='banpanel-form-bantypes'>
                    <RadioGroup
                        aria-labelledby="demo-radio-buttons-group-label"
                        defaultValue="permanent"
                        name="radio-buttons-group"
                        sx={{
                            width: "100%",
                            height: "auto",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            flexDirection: "row"
                        }}
                        onChange={handleChange}
                    >
                        <FormControlLabel value="permanent" control={<Radio />} label="Permanent" />
                        <FormControlLabel value="duration" control={<Radio />} label="Voor tijd" />
                        {value === "duration" ?
                        <LocalizationProvider dateAdapter={AdapterDateFns}>
                        <DateTimePicker
                            label="Selecteer tot wanneer"
                            value={date}
                            onChange={(newValue) => {
                            setDate(newValue);
                            }}
                            renderInput={(params) => <TextField {...params} />}
                        />
                    </LocalizationProvider> :
                    <></>    
                    
                    }
                    </RadioGroup>
                    
                    </div>
                    <Tooltip title='Verban de speler van de server' onClick={() => banPlayer()}>
                        <div className='banpanel-form-button'>Ban Speler</div>
                    </Tooltip>
                </form>
                {error ? 
                <div className='message' style={{color: 'red'}}>{message}</div> :  
                 <div className='message' style={{color: "lime"}}>{message}</div>
                 }
                
            </div>
            
        </>
    );
}
export default BanPanel;
