import './ItemStyle.scss';
const minecraftItems = require('minecraft-items')

function Item(props: {itemstack: any;}){
    const itemstack = minecraftItems.get(props.itemstack.id);
    var base64Icon = 'data:image/png;base64,' + itemstack.icon;
    if (props.itemstack.id === "none"){
        return (
            <div className="item"></div>
        );
    }else{
        return (
            <div className="item">
                <img style={{width: 50, height: 50}} src={base64Icon} />
                <span className='item-amount'>{props.itemstack.Amount}</span>
            </div>
        );
    }
}
export default Item;
