import { useEffect, useState } from "react";
import { socket } from "../../../../socket/socket";
import Player from './Player';

function PlayerList(props: {server: any, onPlayerClick: any, selectedPlayer: any;}){
    const [players, setPlayers] = useState<any>([]);

    useEffect(function loadPlayers(){
        console.log(props.server.Ip === undefined)
        if (props.server.Name !== undefined){
            socket.emit("client:server-player-list", props.server.Name);
        }
    }, [props.server]);
    useEffect(function updatePlayers(){
        socket.on(`server:mcserver-player-list`, data => {
            console.log(data);
            console.log(props.selectedPlayer);

            setPlayers(JSON.parse(data));
        })
    }, []);
    return (
        players.map((player: { UUID: any; Displayname: any; }) => 
            <div key={player.UUID}>
                {
                <Player 
                    player={player} 
                    onPlayerClick={props.onPlayerClick} 
                    selectedPlayer={props.selectedPlayer}
                />
                }
            </div>
        )
    );
}
export default PlayerList;
