import { Tooltip } from '@mui/material';

import './KillPanelStyle.scss';

import { useEffect, useState } from 'react';
import { socket } from '../../../../../context/socket';

function KillPanel(props: {player: any;}){
    const [error, setError] = useState<boolean>(false);
    const [message, setMessage] = useState<string>("");
    const [pmMessage, setPMMessage] = useState<string>("");

    function killPlayer(){
        var data = {
            Player: props.player,
            Feature: "kill",
            Message: pmMessage
        }
        socket.emit("client:features-change", data);
        setPMMessage("");
    }
    useEffect(function listenMessages(){
        socket.on(`server:features-change-message`, data => {
            if (data.includes("fout"))setError(true);
            else setError(false);
            setInfoMessage(data);
        })
    }, []);
    function setInfoMessage(data: string){
        setMessage(data);
        setTimeout(function(){
            if (message !== data)
                setMessage("");
        }, 5000)
    }
    function handleMessageChange (e: any) {
        setPMMessage(e.target.value)
    }
    return (
        <>
            <div className='panel-header'>
                Kick Panel - {props.player.Displayname}
            </div>
            <div className='panel-line'></div>
            <div className='killpanel-container'>
                <form className='killpanel-form'>
                    <input type="text" onChange={handleMessageChange} placeholder="Geef het bericht in dat je wilt dat naar de speler gestuurd wordt (Leeg is niks!)" value={pmMessage} />
                    <Tooltip title='Laat de speler zijn client crashen' onClick={() => killPlayer()}>
                        <div className='killpanel-form-button'>Vermoord Speler</div>
                    </Tooltip>
                </form>
                {error ? 
                <div className='message' style={{color: 'red'}}>{message}</div> :  
                 <div className='message' style={{color: "lime"}}>{message}</div>
                 }
                
            </div>
            
        </>
    );
}
export default KillPanel;
