import { FormControlLabel, Radio, RadioGroup, TextField, Tooltip } from '@mui/material';
import { DatePicker, LocalizationProvider } from '@mui/x-date-pickers';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';

import './BanPanelStyle.scss';

import socketIOClient from "socket.io-client";
import IpAddress from '../../../../../IpAddress';
import { ChangeEvent, useEffect, useState } from 'react';
import { CalendarOrClockPicker } from '@mui/x-date-pickers/internals/components/CalendarOrClockPicker';
var ip = new IpAddress();
let socket = socketIOClient(`http://${ip.getIP()}:3001`)

function BanPanel(props: {player: any;}){
    const [error, setError] = useState<boolean>(false);
    const [message, setMessage] = useState<string>("");
    const [banMessage, setBanMessage] = useState<string>("");
    const [permBan, setPermBan] = useState<boolean>(false);
    const [duration, setDuration] = useState<number>(0);
    const [value, setValue] = useState<Date | null>(null);

    function banPlayer(){
        var data = {
            Player: props.player,
            Feature: "ban",
            Message: banMessage,
            Permban: permBan,
            Duration: duration
        }
        socket.emit("client:features-change", data);
        setBanMessage("");
    }
    useEffect(function listenMessages(){
        socket.on(`server:features-change-message`, data => {
            if (data.includes("fout"))setError(true);
            else setError(false);
            setInfoMessage(data);
        })
    }, []);
    function setInfoMessage(data: string){
        setMessage(data);
        setTimeout(function(){
            if (message !== data)
                setMessage("");
        }, 5000)
    }
    function handleMessageChange (e: any) {
        setBanMessage(e.target.value)
    }
    return (
        <>
            <div className='panel-header'>
                Kick Panel - {props.player.Displayname}
            </div>
            <div className='panel-line'></div>
            <div className='banpanel-container'>
                <form className='banpanel-form'>
                    <input type="text" onChange={handleMessageChange} placeholder="Geef ban message..." value={banMessage} />
                    <div className='banpanel-form-bantypes'>
                    <RadioGroup
                        aria-labelledby="demo-radio-buttons-group-label"
                        defaultValue="permanent"
                        name="radio-buttons-group"
                        sx={{
                            width: "100%",
                            height: "auto",
                            display: "flex",
                            flexDirection: "flex-start",
                            justifyContent: "flex-end",
                            alignItems: "row"
                          }}
                    >
                        <FormControlLabel value="permanent" control={<Radio />} label="Permanent" />
                        <FormControlLabel value="duration" control={<><Radio /></>} label="Voor tijd" />
                            <LocalizationProvider dateAdapter={AdapterDateFns}>
                                <DatePicker
                                    label="Selecteer to wanneer"
                                    value={value}
                                    onChange={(newValue) => {
                                    setValue(newValue);
                                    }}
                                    renderInput={(params) => <TextField {...params} />}
                                />
                            </LocalizationProvider>
                    </RadioGroup>
                    
                    </div>
                    <Tooltip title='Laat de speler zijn client crashen' onClick={() => banPlayer()}>
                        <div className='banpanel-form-button'>Ban Speler</div>
                    </Tooltip>
                </form>
                {error ? 
                <div className='message' style={{color: 'red'}}>{message}</div> :  
                 <div className='message' style={{color: "lime"}}>{message}</div>
                 }
                
            </div>
            
        </>
    );
}
export default BanPanel;
