import { Tooltip } from '@mui/material';
import './ItemStyle.scss';

import { FaTrash } from 'react-icons/fa';
import { FaCopy } from 'react-icons/fa';
import { FaQuidditch } from 'react-icons/fa';

import { Menu, MenuItem, MenuButton, SubMenu } from "@szhsin/react-menu";
import "@szhsin/react-menu/dist/index.css";

function Item(props: {itemstack: any, inventoryAction: any;}){

    function removeItem(){
        var data = {
            Player: props.player,
            Feature: "gamemode",
            Gamemode: gamemode
        }
        socket.emit("client:features-change", data);
    }
    function duplicateItem(){
        console.log("Item duplicated removed")
    }
    function dropItem(){
        console.log("Drop item removed")
    }
    if (props.itemstack === null || props.itemstack.texture === undefined){
        return (
            <div className="item"></div>
        );
    }else{
        return (
            <div>
                <Menu menuButton={
                    <Tooltip placement="top" title={<div className='item-tooltip'><div className='item-title'>{"Item Name: " + props.itemstack.Displayname}</div><div className='item-lore'>{props.itemstack.Lore === "none" || props.itemstack.Lore === undefined ? <></> : "Item Lore: " + props.itemstack.Lore}</div></div>}>
                        <div className="item">
                            <img style={{width: 50, height: 50}} src={props.itemstack.texture} />
                            <span className='item-amount'>{props.itemstack.Amount}</span>
                        </div>
                </Tooltip>
                }>
                    <MenuItem onClick={props.inventoryAction("remove")}><FaTrash /><span>Remove Item</span></MenuItem>
                    <MenuItem onClick={props.inventoryAction("duplicate")}><FaCopy /><span>Duplicate item</span></MenuItem>
                    <MenuItem onClick={props.inventoryAction("drop")}><FaQuidditch /><span>Drop Item</span></MenuItem>
                </Menu>
            </div>
        );
    }
}
export default Item;
