import { useEffect, useState } from "react";
import { socket } from "../../../../context/socket";
import Player from './Player';

function PlayerList(props: {serverid: any, onPlayerClick: any, selectedPlayer: any;}){
    const [players, setPlayers] = useState<any>([]);

    useEffect(function loadPlayers(){
        socket.emit("client:server-player-list", props.serverid)
    }, []);
    useEffect(function updatePlayers(){
        socket.on(`server:mcserver-player-list`, data => {
            var jsonPlayers = JSON.parse(data);
            var [activeplayerOnline, setActiveplayer] = useState<boolean>(false);
            jsonPlayers.map((player: {UUID: string}) => {
                if (player.UUID === props.selectedPlayer.UUID){
                    setActiveplayer(true);
                }
                }
            );
            if (!activeplayerOnline)props.onPlayerClick(null);
            setPlayers(jsonPlayers);
        })
    }, []);
    return (
        players.map((player: { UUID: any; Displayname: any; }) => 
            <div key={player.UUID}>
                {
                <Player 
                    player={player} 
                    onPlayerClick={props.onPlayerClick} 
                    selectedPlayer={props.selectedPlayer}
                />
                }
            </div>
        )
    );
}
export default PlayerList;
