import './PaneStyle.scss';

import FiberNewIcon from '@mui/icons-material/FiberNew';
import { useEffect, useState } from 'react';
import SudoModal from './Modals/SudoModal';

function Sudo(props: {onClick: any, selected: boolean}){
    const [selected, setSelected] = useState<boolean>(false);

    const [message, setMessage] = useState<string>("");

    useEffect(function updateSelectedState(){
        setSelected(props.selected);
    }, [props.selected]);
    return (
        <div className="pane" onClick={() => props.onClick()} style={selected ? {backgroundColor:"green"} : {backgroundColor:"#3180E8"}}>
            <FiberNewIcon />
            <div className='pane-title'>Sudo</div>
            <div className='pane-description'>Laat de speler een chat bericht sturen of een commando uitvoeren</div>
        </div>
    );
}
export default Sudo;
